<div class="tab-pane fade show active" id="tab-pane-setting" role="tabpanel" aria-labelledby="tab-pane-setting" tabindex="0">
    <!-- Access Credentials -->
    <div class="information-box mb-3">
        <div class="info-head p-3">
            <div class="d-flex align-items-center justify-content-between">
                <div class="text-white">Access Credentials</div>
            </div>
        </div>
        <div class="m-3 pb-3">
            <div class="row g-4">
                <form>
                    <div class="row g-4">
                        <div class="col-md-6">
                            <div>
                                <div class="form-floating">
                                    <input type="text" class="form-control" id="givenName"
                                        placeholder="Enter first name">
                                    <label for="givenName">Given Name(s)</label>
                                </div>
                            </div>
                        </div>
                        <div class="col-md-6">
                            <div>
                                <div class="form-floating">
                                    <input type="text" class="form-control" id="lastName"
                                        placeholder="Enter last name">
                                    <label for="lastName">Surname / Last Name</label>
                                </div>
                            </div>
                        </div>
                        <div class="col-md-6">
                            <div>
                                <div class="form-floating">
                                    <input type="text" id="email" value="winecountryweekends@gmail.com"
                                        class="form-control" placeholder="Answer" disabled>
                                    <label for="email">Enter eMail/Username</label>
                                </div>
                            </div>
                        </div>
                        <div class="col-md-6">
                            <div>
                                <div class="form-floating">
                                    <input type="text" class="form-control" id="phone"
                                        placeholder="Enter Phone number">
                                    <label for="phone">Contact Phone</label>
                                </div>
                            </div>
                        </div>
                        <div class="col-12 text-center">
                            <button type="submit" class="btn theme-btn px-5">Update</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>
